import React, { useState, useEffect, useCallback } from 'react';
import './CharacterManager.css';
import { rollAbilityCheck, rollFromNotation } from '../../utils/diceUtils';
import DiceRollModal from '../common/DiceRollModal';
import config from '../../config';

const CharacterManager = ({ user }) => {
  const [characters, setCharacters] = useState([]);
  const [selectedCharacter, setSelectedCharacter] = useState(null);
  const [isCreating, setIsCreating] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const [filter, setFilter] = useState('all');
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [diceRollResult, setDiceRollResult] = useState(null);
  const [rollActionName, setRollActionName] = useState('');
  const [formData, setFormData] = useState({
    name: '',
    character_type: 'NPC',
    description: '',
    stats: {
      strength: 10,
      dexterity: 10,
      constitution: 10,
      intelligence: 10,
      wisdom: 10,
      charisma: 10,
      armorClass: 10,
      hitPoints: 10,
      actions: []
    }
  });
  
  // Fetch characters from the backend
  const fetchCharacters = useCallback(async () => {
    try {
      setLoading(true);
      const response = await fetch(`${config.apiUrl}/api/characters`, {
        method: 'GET',
        credentials: 'include'
      });
      
      if (!response.ok) {
        throw new Error('Failed to load characters');
      }
      
      const data = await response.json();
      setCharacters(data);
    } catch (err) {
      console.error('Failed to fetch characters:', err);
      setError('Failed to load characters. Please try again later.');
    } finally {
      setLoading(false);
    }
  }, []);
  
  useEffect(() => {
    fetchCharacters();
  }, [fetchCharacters]);

  // Roll ability check
  const handleAbilityRoll = (abilityName, abilityScore) => {
    const result = rollAbilityCheck(abilityScore);
    setDiceRollResult(result);
    setRollActionName(`${abilityName} Check`);
  };

  // Roll custom action
  const handleActionRoll = (actionName, actionDescription) => {
    // Try to extract dice notation from action description
    const diceMatch = actionDescription.match(/(\d+d\d+(?:[+-]\d+)?)/i);
    
    if (diceMatch) {
      const result = rollFromNotation(diceMatch[0]);
      if (result) {
        setDiceRollResult(result);
        setRollActionName(actionName);
        return;
      }
    }
    
    // Fallback to 1d20 if no dice notation found
    const result = rollFromNotation('1d20');
    setDiceRollResult(result);
    setRollActionName(actionName);
  };

  // Close dice modal
  const closeDiceModal = () => {
    setDiceRollResult(null);
    setRollActionName('');
  };

  // Create a new character
  const handleCreate = async () => {
    try {
      const response = await fetch(`${config.apiUrl}/api/characters`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include',
        body: JSON.stringify({
          name: formData.name,
          character_type: formData.character_type,
          description: formData.description,
          stats: JSON.stringify(formData.stats)
        })
      });
      
      if (!response.ok) {
        throw new Error('Failed to create character');
      }
      
      const data = await response.json();
      setCharacters([...characters, data]);
      setIsCreating(false);
      resetForm();
      
    } catch (err) {
      console.error('Failed to create character:', err);
      setError('Failed to create character. Please try again.');
    }
  };
  
  // Update existing character
  const handleUpdate = async () => {
    if (!selectedCharacter) return;
    
    try {
      const response = await fetch(`${config.apiUrl}/api/characters/${selectedCharacter.id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include',
        body: JSON.stringify({
          name: formData.name,
          character_type: formData.character_type,
          description: formData.description,
          stats: JSON.stringify(formData.stats)
        })
      });
      
      if (!response.ok) {
        throw new Error('Failed to update character');
      }
      
      const data = await response.json();
      setCharacters(characters.map(char => 
        char.id === selectedCharacter.id ? data : char
      ));
      setIsEditing(false);
      setSelectedCharacter(data);
      
    } catch (err) {
      console.error('Failed to update character:', err);
      setError('Failed to update character. Please try again.');
    }
  };
  
  // Delete character
  const handleDelete = async () => {
    if (!selectedCharacter) return;
    
    if (!window.confirm(`Are you sure you want to delete ${selectedCharacter.name}?`)) {
      return;
    }
    
    try {
      const response = await fetch(`${config.apiUrl}/api/characters/${selectedCharacter.id}`, {
        method: 'DELETE',
        credentials: 'include'
      });
      
      if (!response.ok) {
        throw new Error('Failed to delete character');
      }
      
      setCharacters(characters.filter(char => char.id !== selectedCharacter.id));
      setSelectedCharacter(null);
      
    } catch (err) {
      console.error('Failed to delete character:', err);
      setError('Failed to delete character. Please try again.');
    }
  };
  
  // Reset form data
  const resetForm = () => {
    setFormData({
      name: '',
      character_type: 'NPC',
      description: '',
      stats: {
        strength: 10,
        dexterity: 10,
        constitution: 10,
        intelligence: 10,
        wisdom: 10,
        charisma: 10,
        armorClass: 10,
        hitPoints: 10,
        actions: []
      }
    });
  };
  
  // Handle form input changes
  const handleFormChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };
  
  // Handle stat changes
  const handleStatChange = (statName, value) => {
    setFormData({
      ...formData,
      stats: {
        ...formData.stats,
        [statName]: parseInt(value, 10) || 0
      }
    });
  };
  
  // Add a new action to character
  const handleAddAction = () => {
    setFormData({
      ...formData,
      stats: {
        ...formData.stats,
        actions: [
          ...formData.stats.actions,
          { name: '', description: '' }
        ]
      }
    });
  };
  
  // Update action data
  const handleActionChange = (index, field, value) => {
    const updatedActions = [...formData.stats.actions];
    updatedActions[index] = {
      ...updatedActions[index],
      [field]: value
    };
    
    setFormData({
      ...formData,
      stats: {
        ...formData.stats,
        actions: updatedActions
      }
    });
  };
  
  // Remove an action
  const handleRemoveAction = (index) => {
    const updatedActions = formData.stats.actions.filter((_, i) => i !== index);
    
    setFormData({
      ...formData,
      stats: {
        ...formData.stats,
        actions: updatedActions
      }
    });
  };

  // Start editing character
  const handleEditClick = () => {
    if (!selectedCharacter) return;
    
    // Parse the stats if it's a string
    const stats = typeof selectedCharacter.stats === 'string' 
      ? JSON.parse(selectedCharacter.stats) 
      : selectedCharacter.stats;
    
    setFormData({
      name: selectedCharacter.name,
      character_type: selectedCharacter.character_type,
      description: selectedCharacter.description,
      stats: stats
    });
    
    setIsEditing(true);
  };

  // Filter characters based on search term and type filter
  const filteredCharacters = characters.filter(char => {
    // Filter by character type
    if (filter !== 'all' && char.character_type !== filter) {
      return false;
    }
    
    // Filter by search term
    if (searchTerm && !char.name.toLowerCase().includes(searchTerm.toLowerCase())) {
      return false;
    }
    
    return true;
  });

  // Render the character creation/editing form
  const renderForm = () => {
    return (
      <div className="character-form">
        <h2>{isEditing ? 'Edit Character' : 'Create New Character'}</h2>
        
        <div className="form-group">
          <label htmlFor="name">Name</label>
          <input
            type="text"
            id="name"
            name="name"
            value={formData.name}
            onChange={handleFormChange}
            required
          />
        </div>
        
        <div className="form-group">
          <label htmlFor="character_type">Character Type</label>
          <select
            id="character_type"
            name="character_type"
            value={formData.character_type}
            onChange={handleFormChange}
          >
            <option value="PC">PC</option>
            <option value="NPC">NPC</option>
            <option value="Monster">Monster</option>
          </select>
        </div>
        
        <div className="form-group">
          <label htmlFor="description">Description</label>
          <textarea
            id="description"
            name="description"
            value={formData.description}
            onChange={handleFormChange}
            rows="4"
          />
        </div>
        
        <h3>Stats</h3>
        <div className="stats-grid">
          <div className="stat-item">
            <label htmlFor="strength">STR</label>
            <input
              type="number"
              id="strength"
              value={formData.stats.strength}
              onChange={(e) => handleStatChange('strength', e.target.value)}
              min="1"
            />
          </div>
          
          <div className="stat-item">
            <label htmlFor="dexterity">DEX</label>
            <input
              type="number"
              id="dexterity"
              value={formData.stats.dexterity}
              onChange={(e) => handleStatChange('dexterity', e.target.value)}
              min="1"
            />
          </div>
          
          <div className="stat-item">
            <label htmlFor="constitution">CON</label>
            <input
              type="number"
              id="constitution"
              value={formData.stats.constitution}
              onChange={(e) => handleStatChange('constitution', e.target.value)}
              min="1"
            />
          </div>
          
          <div className="stat-item">
            <label htmlFor="intelligence">INT</label>
            <input
              type="number"
              id="intelligence"
              value={formData.stats.intelligence}
              onChange={(e) => handleStatChange('intelligence', e.target.value)}
              min="1"
            />
          </div>
          
          <div className="stat-item">
            <label htmlFor="wisdom">WIS</label>
            <input
              type="number"
              id="wisdom"
              value={formData.stats.wisdom}
              onChange={(e) => handleStatChange('wisdom', e.target.value)}
              min="1"
            />
          </div>
          
          <div className="stat-item">
            <label htmlFor="charisma">CHA</label>
            <input
              type="number"
              id="charisma"
              value={formData.stats.charisma}
              onChange={(e) => handleStatChange('charisma', e.target.value)}
              min="1"
            />
          </div>
        </div>
        
        <div className="combat-stats">
          <div className="combat-stat">
            <label htmlFor="armorClass">AC</label>
            <input
              type="number"
              id="armorClass"
              value={formData.stats.armorClass}
              onChange={(e) => handleStatChange('armorClass', e.target.value)}
              min="1"
            />
          </div>
          
          <div className="combat-stat">
            <label htmlFor="hitPoints">HP</label>
            <input
              type="number"
              id="hitPoints"
              value={formData.stats.hitPoints}
              onChange={(e) => handleStatChange('hitPoints', e.target.value)}
              min="1"
            />
          </div>
        </div>
        
        <h3>Actions</h3>
        <button type="button" className="add-action-btn" onClick={handleAddAction}>
          Add Action
        </button>
        
        {formData.stats.actions.map((action, index) => (
          <div key={index} className="action-form-item">
            <div className="action-header">
              <h4>Action {index + 1}</h4>
              <button 
                type="button" 
                className="remove-action-btn"
                onClick={() => handleRemoveAction(index)}
              >
                Remove
              </button>
            </div>
            
            <div className="form-group">
              <label htmlFor={`action-name-${index}`}>Name</label>
              <input
                type="text"
                id={`action-name-${index}`}
                value={action.name}
                onChange={(e) => handleActionChange(index, 'name', e.target.value)}
              />
            </div>
            
            <div className="form-group">
              <label htmlFor={`action-desc-${index}`}>Description</label>
              <input
                type="text"
                id={`action-desc-${index}`}
                value={action.description}
                onChange={(e) => handleActionChange(index, 'description', e.target.value)}
              />
            </div>
          </div>
        ))}
        
        <div className="form-actions">
          <button type="button" onClick={isEditing ? handleUpdate : handleCreate}>
            {isEditing ? 'Update Character' : 'Create Character'}
          </button>
          <button type="button" onClick={() => {
            setIsCreating(false);
            setIsEditing(false);
            resetForm();
          }}>
            Cancel
          </button>
        </div>
      </div>
    );
  };

  // Helper function to render character stats with dice rolling
  // In your CharacterManager.js, find the renderCharacterStats function and update it:

  function renderCharacterStats() {
    if (!selectedCharacter) return null;
    
    // Handle both old and new data formats safely
    const getStatValue = (statName, defaultValue = 10) => {
      // Try direct property first
      if (selectedCharacter[statName] !== undefined) {
        return selectedCharacter[statName];
      }
      
      // Try stats object (old format)
      if (selectedCharacter.stats) {
        const stats = typeof selectedCharacter.stats === 'string' 
          ? JSON.parse(selectedCharacter.stats) 
          : selectedCharacter.stats;
        
        // Handle old property names
        if (statName === 'armor_class' && stats.armorClass !== undefined) {
          return stats.armorClass;
        }
        if (statName === 'hit_points' && stats.hitPoints !== undefined) {
          return stats.hitPoints;
        }
        
        return stats[statName] || defaultValue;
      }
      
      return defaultValue;
    };
    
    return (
      <>
        <div className="stats-grid">
          <div className="stat-item" onClick={() => handleAbilityRoll('Strength', getStatValue('strength'))}>
            <span className="stat-label">STR</span>
            <span className="stat-value">{getStatValue('strength')}</span>
          </div>
          <div className="stat-item" onClick={() => handleAbilityRoll('Dexterity', getStatValue('dexterity'))}>
            <span className="stat-label">DEX</span>
            <span className="stat-value">{getStatValue('dexterity')}</span>
          </div>
          <div className="stat-item" onClick={() => handleAbilityRoll('Constitution', getStatValue('constitution'))}>
            <span className="stat-label">CON</span>
            <span className="stat-value">{getStatValue('constitution')}</span>
          </div>
          <div className="stat-item" onClick={() => handleAbilityRoll('Intelligence', getStatValue('intelligence'))}>
            <span className="stat-label">INT</span>
            <span className="stat-value">{getStatValue('intelligence')}</span>
          </div>
          <div className="stat-item" onClick={() => handleAbilityRoll('Wisdom', getStatValue('wisdom'))}>
            <span className="stat-label">WIS</span>
            <span className="stat-value">{getStatValue('wisdom')}</span>
          </div>
          <div className="stat-item" onClick={() => handleAbilityRoll('Charisma', getStatValue('charisma'))}>
            <span className="stat-label">CHA</span>
            <span className="stat-value">{getStatValue('charisma')}</span>
          </div>
        </div>
        
        <div className="combat-stats">
          <div className="combat-stat">
            <span className="stat-label">AC</span>
            <span className="stat-value">{getStatValue('armor_class')}</span>
          </div>
          <div className="combat-stat">
            <span className="stat-label">HP</span>
            <span className="stat-value">{getStatValue('hit_points')}</span>
          </div>
          {selectedCharacter.challenge_rating && (
            <div className="combat-stat">
              <span className="stat-label">CR</span>
              <span className="stat-value">{selectedCharacter.challenge_rating}</span>
            </div>
          )}
        </div>
      </>
    );
  }

  // Main component render
  if (loading) {
    return <div className="loading">Loading characters...</div>;
  }

  return (
    <div className="character-manager">
      <div className="character-sidebar">
        <div className="sidebar-header">
          <h2>Characters</h2>
          <button 
            className="create-btn" 
            onClick={() => {
              setIsCreating(true);
              setSelectedCharacter(null);
              setIsEditing(false);
              resetForm();
            }}
          >
            Create New
          </button>
        </div>
        
        <div className="filter-controls">
          <div className="search-box">
            <input
              type="text"
              placeholder="Search characters..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>
          
          <div className="character-filters">
            <button
              className={filter === 'all' ? 'active' : ''}
              onClick={() => setFilter('all')}
            >
              All
            </button>
            <button
              className={filter === 'PC' ? 'active' : ''}
              onClick={() => setFilter('PC')}
            >
              PCs
            </button>
            <button
              className={filter === 'NPC' ? 'active' : ''}
              onClick={() => setFilter('NPC')}
            >
              NPCs
            </button>
            <button
              className={filter === 'Monster' ? 'active' : ''}
              onClick={() => setFilter('Monster')}
            >
              Monsters
            </button>
          </div>
        </div>
        
        <div className="character-list">
          {filteredCharacters.length === 0 ? (
            <div className="no-characters">
              No characters match your criteria
            </div>
          ) : (
            filteredCharacters.map(character => (
              <div
                key={character.id}
                className={`character-item ${selectedCharacter?.id === character.id ? 'selected' : ''}`}
                onClick={() => {
                  setSelectedCharacter(character);
                  setIsCreating(false);
                  setIsEditing(false);
                }}
              >
                <div className="character-name">{character.name}</div>
                <div className="character-type">{character.character_type}</div>
              </div>
            ))
          )}
        </div>
      </div>
      
      <div className="character-detail-panel">
        {selectedCharacter && !isEditing && !isCreating ? (
          <div className="character-details">
            <div className="detail-header">
              <h2>{selectedCharacter.name}</h2>
              <span className="character-type-tag">{selectedCharacter.character_type}</span>
              
              <div className="detail-actions">
                <button onClick={handleEditClick}>Edit</button>
                <button className="delete-btn" onClick={handleDelete}>Delete</button>
              </div>
            </div>
            
            <div className="character-description">
              <h3>Description</h3>
              <p>{selectedCharacter.description}</p>
            </div>
            
            <div className="character-stats">
              <h3>Stats</h3>
              {renderCharacterStats()}
            </div>
          </div>
        ) : isCreating || isEditing ? (
          renderForm()
        ) : (
          <div className="no-selection">
            <p>Select a character from the list or create a new one</p>
          </div>
        )}
      </div>

      {/* Dice Roll Modal */}
      <DiceRollModal 
        rollResult={diceRollResult}
        onClose={closeDiceModal}
        actionName={rollActionName}
      />
    </div>
  );
};

export default CharacterManager;
